sect2.screen:
#!/bin/sh
if [ ! $# -eq 1 ] ; then echo "Syntax: $0 [directory]" exit fi
for tagfile in $1/*/tagfile; do setdir=`echo ${tagfile} | egrep -o '\w+/tagfile$' | xargs dirname` mkdir ${setdir} cp ${tagfile} ${setdir}/tagfile.org cp ${tagfile} ${setdir} done

#!/bin/sh

if [ ! $# -eq 1 ] ; then
  echo "Syntax: $0 [directory]"
  exit
fi

for tforg in $1/*/tagfile.org ; do
  tf=${tforg%.org}
  rm -f ${tf}
  for package in $(grep -v '^#' ${tforg} | cut -d ':' -f 1) ; do
    if ls /var/log/packages/${package}-[0-9]* &> /dev/null ; then 
      echo "${package}: ADD" >> ${tf}
    else
      echo "${package}: SKP" >> ${tf}
    fi
  done
done
